def lexicographically_minimal_string(s):
    t = []
    u = []
    s = list(s)

    while s or t:
        if not t:
            t.append(s.pop(0))
        elif not s:
            u.append(t.pop())
        else:
            if s[0] <= t[-1]:
                t.append(s.pop(0))
            else:
                u.append(t.pop())

    return ''.join(u)

