def find_smallest_n(m):
    # Function to count the number of valid ways for a given n
    def count_ways(n):
        count = 0
        k = 2
        while k * k * k * k <= n:
            count += n // (k * k * k * k)
            k += 1
        return count

    # Binary search for the smallest n
    left, right = 1, 10**18
    while left < right:
        mid = (left + right) // 2
        if count_ways(mid) < m:
            left = mid + 1
        else:
            right = mid

    if count_ways(left) == m:
        return left
    else:
        return -1

