def can_draw_parallel_lines(n, y):
    if n < 3:
        return "No"

    # Check if we can split the points into two groups with the same slope
    for i in range(1, n):
        for j in range(i + 1, n):
            # Calculate the slope of the line passing through points (1, y[0]) and (i+1, y[i])
            slope1 = (y[i] - y[0]) / (i)
            # Calculate the slope of the line passing through points (1, y[0]) and (j+1, y[j])
            slope2 = (y[j] - y[0]) / (j)

            if slope1 == slope2:
                continue

            # Try to split the points into two groups with these slopes
            group1 = set([0, i])
            group2 = set([j])

            for k in range(1, n):
                if k == i or k == j:
                    continue

                # Check if point (k+1, y[k]) can be added to group1
                if (y[k] - y[0]) / k == slope1:
                    group1.add(k)
                # Check if point (k+1, y[k]) can be added to group2
                elif (y[k] - y[0]) / k == slope2:
                    group2.add(k)
                else:
                    break
            else:
                if len(group1) > 1 and len(group2) > 1:
                    return "Yes"

    return "No"

