    n = len(s)
    if n % 2 != 0:
        return 0

    left_count = [0] * (n + 1)
    right_count = [0] * (n + 1)

    for i in range(n):
        left_count[i + 1] = left_count[i] + (1 if s[i] == '(' else 0)

    for i in range(n - 1, -1, -1):
        right_count[i] = right_count[i + 1] + (1 if s[i] == ')' else 0)

    result = 0
    for i in range(n):
