def find_permutations(N, K, matrix):
    def can_swap_columns(x, y):
        for i in range(N):
            if matrix[i][x] + matrix[i][y] > K:
                return False
        return True

    def can_swap_rows(x, y):
        for i in range(N):
            if matrix[x][i] + matrix[y][i] > K:
                return False
        return True

    def find_swappable_groups(can_swap_func):
        parent = list(range(N))

        def find(x):
            if parent[x] != x:
                parent[x] = find(parent[x])
            return parent[x]

        def union(x, y):
            rootX = find(x)
            rootY = find(y)
            if rootX != rootY:
                parent[rootY] = rootX

        for i in range(N):
            for j in range(i + 1, N):
                if can_swap_func(i, j):
                    union(i, j)

        groups = {}
        for i in range(N):
            root = find(i)
            if root not in groups:
                groups[root] = []
            groups[root].append(i)

        return groups

    def factorial(n):
        result = 1
        for i in range(2, n + 1):
            result = (result * i) % MOD
        return result

    column_groups = find_swappable_groups(can_swap_columns)
    row_groups = find_swappable_groups(can_swap_rows)

    column_permutations = 1
    for group in column_groups.values():
        column_permutations = (column_permutations * factorial(len(group))) % MOD

    row_permutations = 1
    for group in row_groups.values():
        row_permutations = (row_permutations * factorial(len(group))) % MOD

    return (column_permutations * row_permutations) % MOD

