import math
import heapq

def is_square(x):
    if x < 0:
        return False
    root = int(math.isqrt(x))
    return root * root == x

def min_moves_to_balance(n, candies):
    square_piles = []
    non_square_piles = []

    for candy in candies:
        if is_square(candy):
            square_piles.append(candy)
        else:
            non_square_piles.append(candy)

    half_n = n // 2
    if len(square_piles) == half_n and len(non_square_piles) == half_n:
        return 0

    if len(square_piles) > half_n:
        excess = len(square_piles) - half_n
        moves = []
        for candy in square_piles:
            if candy == 0:
                moves.append(2)
            else:
                root = int(math.isqrt(candy))
                moves.append(min(candy - root * root, (root + 1) * (root + 1) - candy))
        heapq.heapify(moves)
        return sum(heapq.nsmallest(excess, moves))

    if len(non_square_piles) > half_n:
        excess = len(non_square_piles) - half_n
        moves = []
        for candy in non_square_piles:
            root = int(math.isqrt(candy))
            moves.append(min(candy - root * root, (root + 1) * (root + 1) - candy))
        heapq.heapify(moves)
        return sum(heapq.nsmallest(excess, moves))

    deficit = half_n - len(square_piles)
    moves = []
    for candy in non_square_piles:
        root = int(math.isqrt(candy))
        moves.append(min(candy - root * root, (root + 1) * (root + 1) - candy))
    heapq.heapify(moves)
    return sum(heapq.nsmallest(deficit, moves))

