MOD = 10**9 + 7

def count_valid_sequences(n, m, s):
    # Calculate the balance and minimum balance of the given string s
    balance = 0
    min_balance = 0
    for char in s:
        if char == '(':
            balance += 1
        else:
            balance -= 1
        min_balance = min(min_balance, balance)

    # If the string s itself is invalid, return 0
    if balance < 0:
        return 0

    # Precompute factorials and inverse factorials for binomial coefficients
    fact = [1] * (n + 1)
    inv_fact = [1] * (n + 1)
    for i in range(2, n + 1):
        fact[i] = fact[i - 1] * i % MOD
    inv_fact[n] = pow(fact[n], MOD - 2, MOD)
    for i in range(n - 1, 0, -1):
        inv_fact[i] = inv_fact[i + 1] * (i + 1) % MOD

    def binom(x, y):
        if x < 0 or y < 0 or x < y:
            return 0
        return fact[x] * inv_fact[y] % MOD * inv_fact[x - y] % MOD

    # Count valid pairs (p, q)
    result = 0
    for p_len in range(n - m + 1):
        q_len = n - m - p_len
        for p_balance in range(-min_balance, p_len + 1):
            if p_balance + balance <= q_len:
                result = (result + binom(p_len, p_balance) * binom(q_len, p_balance + balance)) % MOD

    return result

# Read input
n, m = map(int, input().split())
