def construct_graph(n, degrees):
    max_degree = degrees[-1]
    num_vertices = max_degree + 1
    edges = []

    # Create a list of vertices for each degree
    degree_vertices = {d: [] for d in degrees}
    for i in range(1, num_vertices + 1):
        for d in degrees:
            if len(degree_vertices[d]) < d:
                degree_vertices[d].append(i)
                break

    # Connect vertices to form the required degrees
    for d in degrees:
        vertices = degree_vertices[d]
        for i in range(len(vertices)):
            for j in range(i + 1, len(vertices)):
                edges.append((vertices[i], vertices[j]))

    return edges

