class IntervalGraph:
    def __init__(self):
        self.intervals = []
        self.graph = []

    def add_interval(self, x, y):
        self.intervals.append((x, y))
        self.graph.append([])

        for i in range(len(self.intervals) - 1):
            a, b = self.intervals[i]
            if (x < a < y) or (x < b < y):
                self.graph[-1].append(i)
            if (a < x < b) or (a < y < b):
                self.graph[i].append(len(self.intervals) - 1)

    def can_reach(self, start, end):
        visited = [False] * len(self.intervals)
        return self.dfs(start - 1, end - 1, visited)

    def dfs(self, current, target, visited):
        if current == target:
            return True
        visited[current] = True
        for neighbor in self.graph[current]:
            if not visited[neighbor]:
                if self.dfs(neighbor, target, visited):
                    return True
        return False

