def max_dollars(n, board):
    # Create two dictionaries to store the sum of values on each diagonal
    diag1 = {}
    diag2 = {}

    # Calculate the sum of values on each diagonal
    for i in range(n):
        for j in range(n):
            if i + j not in diag1:
                diag1[i + j] = 0
            if i - j not in diag2:
                diag2[i - j] = 0
            diag1[i + j] += board[i][j]
            diag2[i - j] += board[i][j]

    # Find the maximum sum of values for two bishops on different diagonals
    max_sum = 0
    pos1 = (0, 0)
    pos2 = (0, 0)

    for i in range(n):
        for j in range(n):
            for k in range(n):
                for l in range(n):
                    if (i + j != k + l) and (i - j != k - l):
                        current_sum = diag1[i + j] + diag2[i - j] + diag1[k + l] + diag2[k - l] - board[i][j] - board[k][l]
                        if current_sum > max_sum:
                            max_sum = current_sum
                            pos1 = (i + 1, j + 1)
                            pos2 = (k + 1, l + 1)

    return max_sum, pos1, pos2

